import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public title: string = 'Aplicación Bielera';
  public counter: number = 10;

  increaseBy() {
    this.counter
  }
}
<h1> {{title}} </h1>
<hr>
<h3>Counter:{{counter}}</h3>
<button>+1</button>
<button>-1</button>
