import { Component } from '@angular/core';


//hace la  referencia  a cada  componente
@Component({
  selector: 'app-heroes-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})

export class HeroComponent {

  public name: string = 'Cerverza Club';
  public name1: string = 'Cerverza Pilsener';
  public name2: string = 'Cerverza Barril';
  public name3: string = 'Cerverza Alemana';
  public age: number = 45;

  get capitalizeName(): string{
    return 'Cervez gratis'
  }
}
<h1>{{ name }}</h1>
<dl>
  <td>Nombre de la Cerveza</td>
  <dd>{{ name3 }}</dd>

    <td>Tiempo de Añejamiento</td>
  <dd>{{ name1 }}</dd>

    <td>Correo</td>
  <dd>telefono</dd>

    <td>celular</td>
  <dd>Fecha de  nacimiento</dd>

      <td>Método de Preparación:</td>
  <dd>Algun Metodo </dd>

      <td>Capitalizado:</td>
  <dd>{{capitalizeName}} </dd>

</dl>

<button class="btn btn-primary mx-2">
  Cambiar Nombre
</button>
<button  class="btn btn-primary">
  Cambiar edad
</button>
